//Back-end complete function Template for C++
class Solution {
  public:
    long long int gcd(long long int a, long long int b) { 
    if (a == 0) 
        return b; 
    return gcd(b%a, a); 
}
    long long int commDiv(long long int a,long long int b) {
    // find gcd of a,b
    long long int n = gcd(a, b);
    // Count divisors of n.
    long long int result = 0;
    for (long long int i=1; i<=sqrt(n); i++){
        // if 'i' is factor of n
        if (n%i==0){
            // check if divisors are equal
            if (n/i == i)
                result += 1;
            else
                result += 2;
        }
    }
    return result;
}
};

//User function Template for C#
class Solution
{
    //Function to calculate gcd
    public long gcd(long a, long b) 
    { 
        if (a == 0) 
            return b; 
        return gcd(b % a, a); 
    }

    //Function to calculate number of common divisors.
    public long commDiv(long a, long b)
    {
        //Finding gcd of a and b.
        long n = gcd(a, b);

        //Counting divisors of n.
        long result = 0;
        for (long i = 1; i <= Math.Sqrt(n); i++)
        {
            if (n % i == 0)
            {
                //Checking if divisors are equal.
                if (n / i == i)
                    result++;
                else
                    result += 2; //Since both i and n/i are divisors.
            }
        }

        return result;
    }
}
