// Function to compare two strings of equal length and return 1 if a is less than b, 2 if a is greater than b, and 3 if they are equal
int check(string a, string b) {
    // Calculate the absolute difference in length between a and b
    int x = abs((int)a.length() - (int)b.length());

    // Create a string of zeros with length x
    string temp(x, '0');

    // Pad the shorter string with zeros at the beginning to make their lengths equal
    if ((int)a.length() < (int)b.length())
        a = temp + a; // concatenate temp and a
    else
        b = temp + b;

    // Compare the padded strings lexically and return the corresponding value
    return (a < b ? 1 : a > b ? 2 : 3);
}

