class Solution
{
    int check (String a, String b)
    {
        // initializations
        int i=0,j=0;

        // remove leading zeros in string a
        while(i<a.length()&&a.charAt(i)=='0')
            i++;

        // remove leading zeros in string b
        while(j<b.length()&&b.charAt(j)=='0')
            j++;

        // check if lengths of a and b are equal after removing leading zeros
        if(a.length()-i==b.length()-j)
        {
            // check each character in a and b after removing leading zeros
            for(;i<a.length();i++,j++)
            {
                // characters are equal, continue to next character
                if(a.charAt(i)==b.charAt(j))
                    continue;

                // character in a is greater than character in b, return 2
                if(a.charAt(i)>b.charAt(j))
                    return 2;

                // character in a is less than character in b, return 1
                else
                    return 1;
            }
            // strings are equal, return 3
            return 3;
        }

        // length of a after removing leading zeros is greater than length of b, return 2
        if(a.length()-i>b.length()-j)
            return 2;

        // length of a after removing leading zeros is less than length of b, return 1
        return 1;
    }
}

