class Solution {
    public int findDifference(int[] arr1, int[] arr2) {
        int n = arr1.length;
        int res = 0;

        // Loop through the arrays and compare elements
        for (int i = 0; i < n; i++) {
            // If elements at the current position are not the same, increment res
            if (arr1[i] != arr2[i]) {
                res++;
            }
        }
        return res;
    }
}

