class Solution {
  public:
    // Function to compare scores of two players.
    vector<int> scores(vector<int>& arr1, vector<int>& arr2) {
        vector<int> v;
        int ca = 0, cb = 0;
        // iterating over the scores of the two players.
        for (int i = 0; i < 3; i++) {
            // if score of player A is greater than score of player B,
            // increment count of A.
            if (arr1[i] > arr2[i])
                ca++;
            // if score of player B is greater than score of player A,
            // increment count of B.
            else if (arr2[i] > arr1[i])
                cb++;
        }
        v.push_back(ca);
        v.push_back(cb);
        return v;
    }
};

