cpp.full_func:
class Solution {
  public:
    int getCompundInterest(int P, int T , int N , int R) {
        
        int amt = (double)P * pow( (1 + ((double)R / 100) / (double)N ), (double)N * (double)T);
        
        return amt;
    }
};

java.full_func:
//Calculating compound interest using the formula and returning the amount
class Solution {
    static int getCompundInterest(int P , int T , int N , int R) {
        //Calculating amount using the formula for compound interest
        int amt = (int)((double)P * Math.pow( (1 + ((double)R / 100) / (double)N ), (double)N * (double)T));
        //Returning the calculated amount
        return amt;
    }
};

