//User function Template for javascript
/**
 * @param {number} P
 * @param {number} T
 * @param {number} N
 * @param {number} R
 * @returns {number}
*/

class Solution {
    //Function to get compound interest.
    getCompundInterest(P, T, N, R) {
        let A = P * Math.pow((1 + (R / 100) / N), N * T);

        return parseInt(A);
    }
}
