class Solution {
  public:
    string removeConsecutiveCharacter(string& s) {
        // only entering those elements in stack which are unique consecutively.
        stack<char> st;
        for (int i = 0; i < s.length(); i++) {
            if ((st.empty() == 0 && st.top() != s[i]) || st.empty() == 1)
                st.push(s[i]);
        }
        // storing stack elements in string.
        string res;
        while (st.empty() == 0) {
            res += st.top();
            st.pop();
        }

        // reversing the string because stack is a LIFO data structure.
        reverse(res.begin(), res.end());
        return res;
    }
};

java.full_func:
class Solution {
    public String removeConsecutiveCharacter(String s) {
        // Stack to store unique consecutive characters
        Stack<Character> st = new Stack<>();

        // Only pushing unique consecutive characters into the stack
        for (int i = 0; i < s.length(); i++) {
            if ((st.isEmpty() == false && st.peek() != s.charAt(i)) ||
                st.isEmpty() == true) {
                st.push(s.charAt(i));
            }
        }

        // Storing stack elements in a StringBuilder (more efficient than String
        // concatenation)
        StringBuilder res = new StringBuilder();
        while (!st.isEmpty()) {
            res.append(st.pop());
        }

        // Reversing the string since stack is LIFO (Last In First Out)
        return res.reverse().toString();
    }
}

