class Solution {
  public:
    // Helper method to check if a number is a power of two.
    bool IsPowerOfTwo(int x)
    {
        return (x != 0) && ((x & (x - 1)) == 0);
    }
    
    string isSumOfConsecutive(int N) {
        
        // If a number is a power of two, it can't be expressed as sum of
        // atleast two consecutive numbers, else it's always possible.
        if(IsPowerOfTwo(N))
            return "No";
        else
            return "Yes";
    }
};

