class Solution
{
    public void checkString(string s)
    {
        int v = 0; // will store vowels count
        int c = 0; // will store consonants count

        // calculating vowel and consonant counts
        for (int i = 0; i < s.Length; i++)
        {
            char ch = Char.ToLower(s[i]); // Convert to lowercase to handle case-insensitive comparison
            if (ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u')
            {
                v++;
            }
            else if (ch >= 'a' && ch <= 'z') // Check if ch is an alphabet character but not a vowel
            {
                c++;
            }
        }

        if (v > c)
            Console.WriteLine("Yes");
        else if (c > v)
            Console.WriteLine("No");
        else
            Console.WriteLine("Same");
    }
}
