class Solution
{
    // Function to convert a given number to Roman numeral
    String convertToRoman(int n)
    {
        // 2D array to store the Roman numeral values for each digit
        String strrom[][] = {{"","I","II","III","IV","V","VI","VII","VIII","IX"},
                             {"","X","XX","XXX","XL","L","LX","LXX","LXXX","XC"},
                             {"","C","CC","CCC","CD","D","DC","DCC","DCCC","CM"},
                             {"","M","MM","MMM","","","","","",""}};
        
        // Variables for iterating through the digits and building the Roman numeral string
        int i = 0;
        String str = "";
        
        // Convert the given number to Roman numeral
        while(n>0)
        {
            // Get the digit at the current position and append its corresponding Roman numeral value to the string
            str = strrom[i][n%10]+str;
            
            // Move to the next digit by dividing the number by 10
            n = n / 10;
            
            // Move to the next row in the 2D array to access the next digit
            i++;
        }
        
        // Return the final Roman numeral string
        return str;
    }
