class Solution {
    min_operations(nums, n) {
        let dp = new Array(n);
        for (let i = 0; i < n; i++) {
            dp[i] = 1;
        }
        for (let i = 1; i < n; i++) {
            let temp = 0;
            for (let j = 0; j < i; j++) {
                // If nums[i] is greater than nums[j] then it can be added to the set of
                // LIS
                if (nums[i] > nums[j])
                    if (nums[i] - nums[j] > i - j - 1) temp = Math.max(temp, dp[j]);
            }
            // length of maximum increasing subsequnce whose last element is nums[i]
            dp[i] = Math.max(dp[i], 1 + temp);
        }
        // total opeartion needs to  apply
        let res = 0;
        for (let i = 0; i < n; i++) {
            res = Math.max(res, dp[i]);
        }
        return n - res;
    }
}
