class Solution{
   	public:
   	// Function to calculate the sum of the first and last digit of a number
   	int corner_digitSum(int n){
   		int sum = 0;
   		// Adding the last digit to the sum
   		sum += n%10;
   		// Removing the last digit from the number
   		n /= 10;
   		// Removing all the digits except the first one
   		while(n >= 10)n/=10;
   		// Adding the first digit to the sum
   		sum += n;
   		// Returning the sum of the first and last digit
   		return sum;   		
   	}    
};

