//User function Template for C#

class Solution
{
    public List<KeyValuePair<int, int>> corres_Pair(int[] arr1, int[] arr2, int N)
    {
        List<KeyValuePair<int, int>> res = new List<KeyValuePair<int, int>>();
        for (int i = 0; i < N; i++)
        {
            res.Add(new KeyValuePair<int, int>(arr1[i], arr2[i]));
        }
        return res;
    }
}
