// Function to count the number of lowercase and uppercase characters in a string
int Count(string S){
    int ans = 0;
    // Iterate over each character in the string
    for(auto i : S){
        // Check if the character is a lowercase or uppercase letter and increment the count if true
        if(i >= 'a' and i <= 'z') ans++;
        else if(i >= 'A' and i <= 'Z') ans++;
    }
    // Return the final count
    return ans;
}

//User function Template for C#

class Solution
{
    //Complete this function
    //Function to count the number of characters in a string.
    public int Count(string S)
    {
        int count = 0;
        // Iterate over each character in the string
        foreach(char c in S)
        {
            // Check if the character is a lowercase or uppercase letter and increment the count if true
            if(c >= 'a' && c <= 'z')
                count++;
            else if(c >= 'A' && c <= 'Z')
                count++;
        }
        // Return the final count
        return count;
    }

}
