public:
    int facDigits(int N){
        // factorial of -ve number  
        // doesn't exists 
        if (N < 0) 
        return 0; 
        
        // base case 
        if (N <= 1) 
        return 1; 
        
        // Use Kamenetsky formula to calculate 
        // the number of digits 
        double x = ((N * log10(N / M_E) +  
