class Solution{
	public:
	using ll = long long int;
	ll mod = 1e9 + 7;
	
	// Function to calculate modular exponentiation
	ll modpow(ll a, ll b)
	{
	    ll x = 1%mod;
	    a %= mod;
	    
	    while(b)
	    {
	        if(b&1)
	            x = (x*a)%mod;
	        a = (a*a)%mod;
	        b >>= 1;
	    }
	    
	    return x;
	}
	
	// Function to calculate modular inverse
	ll modinverse(ll a)
	{
	    return modpow(a,mod-2);
	}
	
	// Function to compute the value
	int compute_value(int n)
	{
		if(n==1)
			return 2;
			
		ll val=2;
		
		for(ll i=1;i<n;i+=1)
		{
		   val=(val%mod*((2*i)%mod+1)%mod*2)%mod;
		   ll x=modinverse(i+1);
		   val=(val*x)%mod;
		}
		
		return (int)val;
	}
};

class Solution
{
    static long mod=1000000007; // defining the value of modulo as a constant

    // function to compute the value based on input
    public int compute_value(int n)
    {
        if(n==1) // check if n is equal to 1
		return 2; // return 2

		long val=2; // initialize val as 2
		for(long i=1;i<n;i+=1) // loop from 1 to n-1
		{
		   val=(val%mod*((2*i)%mod+1)%mod*2)%mod; // calculate the value using modulo arithmetic
		   long x=modpwr(i+1,mod-2); // calculate the power of i+1 modulo mod-2
		   val=(val*x)%mod; // multiply the value with x and take the modulo
		}
		return (int)val; // return the final calculated value as an integer
    }
	
	// function to compute the modulo power
	public static long modpwr(long m,long n)
	{
		long res=1; // initialize res as 1
		m=m%mod; // take modulo of m
		if(m==0) // check if m is equal to 0
		return 0; // return 0

		while(n>0) // loop while n is greater than 0
		{
			if((n&1)!=0) // bitwise AND operation with 1 to check if n is odd
			{
				res=(res*m)%mod; // multiply res with m and take the modulo
			}
			n=n>>1; // right shift n by 1 bit
			m=(m*m)%mod; // square m and take the modulo
		}
		return res; // return the final calculated value
	}
}

class Solution
{
    public long mod = 1000000007;

    public long modpow(long a, long b)
    {
        long x = 1 % mod;
        a %= mod;

        while (b > 0)
        {
            if ((b & 1) == 1)
                x = (x * a) % mod;
            a = (a * a) % mod;
            b >>= 1;
        }

        return x;
    }

    public long modinverse(long a)
    {
        return modpow(a, mod-2);
    }

    public int compute_value(int n)
    {
        if (n == 1)
            return 2;

        long val = 2;

        for (long i = 1; i < n; i += 1)
        {
           val = (val % mod * ((2 * i) % mod + 1) % mod * 2) % mod;
           long x = modinverse(i + 1);
           val = (val * x) % mod;
        }

        return (int)val;
    }
}
