// Function to count the number of even digit substrings in a given string
long long evenNumSubstring(string S) {
    int len = S.length(); // length of the input string
    long long count = 0; // variable to store the count of even digit substrings

    // Iterate through each character in the string
    for (int i = 0; i < len; i++) {
        int temp = S[i] - '0'; // convert the current character to integer

        // Check if the current digit is even
        if (temp % 2 == 0) 
            count += (i + 1); // if even, add the current index to the count
    }

    return count; // return the total count of even digit substrings
}

class Solution
{
    //Complete the function here
    public long evenNumSubstring(string S)
    {
        int len = S.Length; // length of the input string
        long count = 0; // variable to store the count of even digit substrings

        // Iterate through each character in the string
        for (int i = 0; i < len; i++)
        {
            int temp = int.Parse(S[i].ToString()); // convert the current character to integer

            // Check if the current digit is even
            if (temp % 2 == 0)
                count += (i + 1); // if even, add the current index to the count
        }

        return count; // return the total count of even digit substrings
    }
}
