class Solution {
  public:
    int gcd(int a, int b) { 
        if (b == 0) 
           return a; 
        return gcd(b, a%b); 
    } 
    
    int countIntegralPoints(int x1, int y1, int x2, int y2) {
        
        // If both the points are in same coordinates, then answer will be 0.
        if (x1==x2 && y1==y2)
            return 0;
        
        // If line joining p and q is parallel to
        // x axis, then count is difference of y
        // values
        if (x1==x2)
            return abs(y1 - y2) - 1;
    
        // If line joining p and q is parallel to
        // y axis, then count is difference of x
        // values
        if (y1 == y2)
            return abs(x1-x2) - 1;
    
        return gcd(abs(x1-x2), abs(y1-y2))-1;
    }
};

class Solution
{
    public int countIntegralPoints(int x1, int y1, int x2, int y2)
    {
        if (x1 == x2 && y1 == y2)
            return 0;

        if (x1 == x2)
            return Math.Abs(y1 - y2) - 1;

        if (y1 == y2)
            return Math.Abs(x1 - x2) - 1;

        return GCD(Math.Abs(x1 - x2), Math.Abs(y1 - y2)) - 1;
    }

    private int GCD(int a, int b)
    {
        if (b == 0)
            return a;
        return GCD(b, a % b);
    }
}
