// Back-end complete function Template for C++

class Solution{
public:
    // Function to count the number of decimal places in a division
    int countDecimal(int A, int B){
        unordered_map<int, int> mp;
        int ans = 0;
        while(A%B != 0){
            A = A%B;
            ans++;
            // Check if A has been encountered before, which means there is a repeating decimal
            if(mp.find(A) != mp.end())
                return -1;
            mp[A] = 1;
            // Multiply A by 10 to consider the next decimal place
            A *= 10;
        }
        return ans;
    }
};

//User function Template for C#

class Solution{
    public int countDecimal(int A, int B){
        Dictionary<int, int> mp = new Dictionary<int, int>();
        int ans = 0;
        while(A%B != 0){
            A = A%B;
            ans++;
            if(mp.ContainsKey(A)){
                return -1;
            }
            mp[A] = 1;
            A *= 10;
        }
        return ans;
    }
}
