class Solution {
  public:

    // helper method to check if there's a 4 digit in the number
    int check(int n) {
        while (n) {
            if (n % 10 == 4)
                return 1;
            n /= 10;
        }
        return 0;
    }

    int countNumberswith4(int n) {

        int ans = 0; // initialize ans as 0.

        for (int i = 0; i <= n; i++) {
            if (check(i))
                ans++; // Incrementing the count on finding a number with the digit 4.
        }

        return ans;
    }
};

