class Solution {
    /**
     * @param {number} n
     * @returns {number}
     */
    countNumberswith4(n) {
        let ans = 0; // Initialize ans as 0.

        for (let i = 0; i <= n; i++) {
            if (this.check(i))
                ans++; // Incrementing the count on finding a number with the digit 4
        }

        return ans;
    }

    // Helper method to check if there's a 4 digit in the number
    check(num) {
        while (num !== 0) {
            if (num % 10 === 4) return true;
            num = Math.floor(num / 10);
        }
        return false;
    }
}
