//Back-end complete function Template for C++
class Solution {
  public:
    //Function to count the number of integers divisible by M in the range A to B.
    int countDivisibles(int A, int B, int M) {
    //Checking if A is divisible by M.
    if (A % M == 0)
        //Returning the count of numbers evenly divisible by M in the range (B/M - A/M) + 1.
        return (B / M) - (A / M) + 1;

    //Returning the count of numbers evenly divisible by M in the range (B/M - A/M).
    return (B / M) - (A / M);
}
};

