class Solution {
  public:
    // Function to count numbers in range [L, R] that are divisible by 3
    int count3DivNums(long long L, long long R) {
        
        // Boolean array to mark composite numbers
        bool composite[31625];
        // Array to store cumulative count of numbers divisible by 3
        long long dp[31625]={0};
        
        // Marking 0 and 1 as composite
        composite[0]=composite[1]=true;
        dp[0]=dp[1]=0;
        
        // Sieve of Eratosthenes to find composite numbers
        for(int i = 2; i<31625; i++)
        {
            dp[i]+=dp[i-1];
            
            // If current number is prime
            if(!composite[i])
            {
                // Increment the cumulative count
                ++dp[i];
                
                // Mark all multiples of the prime number as composite
                for(int j = i*2; j<31625; j+=i)
                    composite[j]=true;
            }
        }
        
        // Finding the floor of square root of L and R
        long long prevl = L;
        L = (int)(sqrt(L));
        
        // Adjusting L in case its square is less than L
        if(L*L < prevl)
            ++L;
        // Finding the floor of square root of R
        R = (int)(sqrt(R));
        
        // Count of numbers divisible by 3 in the range [L, R]
        int ct = dp[R]-dp[L-1];
        
        return ct;
    }
};
