class Solution {
  public:
    // Returns count of numbers with k as last 
    // digit. 
    // Function to count the numbers with K as last digit given a range

    int countLastDigitK(long long low, long long high, long long K) {
        
        // Finding the next multiple of 10 greater than or equal to the lower bound
        long long mlow = 10 * ceil(low/10.0);

        // Finding the previous multiple of 10 lesser than or equal to the upper bound
        long long mhigh = 10 * floor(high/10.0);
        
        // Calculating the number of multiples of 10 within the given range
        int count = (mhigh - mlow)/10; 
        
        // Checking if the upper bound has the last digit equal to K, incrementing count if true
        if (high % 10 >= K) 
            count++; 

        // Checking if the lower bound has the last digit equal to K and is not divisible by 10, incrementing count if true    
        if (low % 10 <=K && (low%10))
            count++;
            
        // returning the count of numbers with K as last digit
        return count; 
    }
};

