// Function to count the number of odd and even elements in an array
class Solution {
  public:
    void countOddEven(vector<int>& arr) {
        int odd = 0, even = 0; // variables to store the count of odd and even elements
        int n = arr.size();
        // iterating over the array
        for (int i = 0; i < n; i++) {

            // checking if the current element is even or odd
            if (arr[i] % 2 == 0)
                even++; // incrementing even count if the element is even
            else
                odd++; // incrementing odd count if the element is odd
        }

        cout << odd << " " << even
             << endl; // printing the count of odd and even elements
    }
};

