//Back-end complete function Template for C++

class Solution {
  public:
    pair<int, int> countOddEven(vector<int> &arr) {

        int countOdd = 0, countEven = 0, sizeof_array = arr.size();

        // iterating through the array
        for (int i = 0; i < sizeof_array; i++) {
            // checking if the element is even
            if (arr[i] % 2 == 0) {
                countEven++;
            }
            // if not even, it must be odd
            else {
                countOdd++;
            }
        }

        return {countOdd, countEven};
    }
};

class Solution {
    // Function to count even and odd elements in the array
    // arr: Array with its elements
    public int[] countOddEven(int[] arr) {
        // Initializing count variables for odd and even elements
        int countOdd = 0, countEven = 0, sizeof_array = arr.length;

        // Iterating through the array
        for (int i = 0; i < sizeof_array; i++) {
            // Checking if the element is even
            if (arr[i] % 2 == 0) {
                countEven++;
            }
            // If not even, it must be odd
            else {
                countOdd++;
            }
        }

        // Returning an array containing the count of odd and even elements
        return new int[] {countOdd, countEven};
    }
}

class Solution:

    def countOddEven(self, arr):
        # Initializing count variables for odd and even elements
        countOdd = 0
        countEven = 0
        sizeof_array = len(arr)

        # Iterating through the array
        for i in range(sizeof_array):
            # Checking if the element is even
            if arr[i] % 2 == 0:
                countEven += 1
            # If not even, it must be odd
            else:
                countOdd += 1

        # Returning a list containing the count of odd and even elements
        return [countOdd, countEven]

//Back-end complete function Template for javascript
class Solution {
    // Function to count the number of odd and even elements in the array.
    countOddEven(arr) {
        let n = arr.length;
        let cnt = 0; // Initialize a variable to store the count of odd elements.
        for (let a of arr) cnt += (a % 2); // Increment the count if the element is odd.

        return [
            cnt, n - cnt
        ]; // Return the count of odd and even elements as an array.
    }
}
