// Function to count the number of elements in the array that are less than or equal to
// x
class Solution {
  public:
    int countOfElements(int x, vector<int> &arr) {

        int n = arr.size();
        int count = 0; // Initialize count variable

        // Iterate through the array
        for (int i = 0; i < n; i++) {
            if (arr[i] <= x)
                count++; // Increment count if current element is less than or equal to
                         // x
        }

        return count; // Return the count of elements
    }
};

java.full_func:
class Solution {
    public int countOfElements(int x, List<Integer> arr) {
        int count = 0; // Initialize count variable
        int n = arr.size();

        // Iterate through the array
        for (int i = 0; i < n; i++) {
            if (arr.get(i) <= x)
                count++; // Increment count if current element is less than or equal to
                         // x
        }

        return count; // Return the count of elements
    }
}

