// User function Template for javascript

class Solution {
    countOfElements(x, arr) {
        let count = 0; // Initialize count variable
        let n = arr.length;

        // Iterate through the array
        for (let i = 0; i < n; i++) {
            if (arr[i] <= x)
                count++; // Increment count if current element is less than or equal to
                         // x
        }

        return count; // Return the count of elements
    }
}
