class Solution {
  public:
    long long countOddXorPairs(vector<int>& arr) {
        long long oddCount = 0, evenCount = 0;

        // Count the number of odd and even elements in the array
        for (int num : arr) {
            if (num % 2 == 0) {
                evenCount++;
            } else {
                oddCount++;
            }
        }

        // The number of pairs with odd XOR will be the product of the count of odd and
        // even elements
        return oddCount * evenCount;
    }
};

