class Solution {
  public:
    // Function to count pairs for given A and B
    int countPairs(int A, int B) {
        
        int count=0;
		int b;
		// Looping over possible values of a from -100 to 100
		for(int a=-100; a<=100; a++)
		{
			// Calculating b based on a and A
			b = A-a;
			
			// Checking if the equation a^3 + b^3 equals B, increment count if true
			if(a*a*a+b*b*b == B)
				count++;
		}
		
		return count;
    }
};

