void merge(int arr[], int l, int m, int r) {
    int i, j, k;
    int n1 = m - l + 1;
    int n2 = r - m;

    // Create temp arrays
    int L[n1], R[n2];

    // Copy data to temp arrays L[] and R[]
    for (i = 0; i < n1; i++)
        L[i] = arr[l + i];
    for (j = 0; j < n2; j++)
        R[j] = arr[m + 1 + j];

    // Merge the temp arrays back into arr[l..r]
    i = 0;
    j = 0;
    k = l;
    while (i < n1 && j < n2) {
        if (L[i] <= R[j]) {
            arr[k] = L[i];
            i++;
        } else {
            arr[k] = R[j];
            j++;
        }
        k++;
    }

    // Copy the remaining elements of L[], if there are any
    while (i < n1) {
        arr[k] = L[i];
        i++;
        k++;
    }

    // Copy the remaining elements of R[], if there are any
    while (j < n2) {
        arr[k] = R[j];
        j++;
        k++;
    }
}

void mergeSort(int arr[], int l, int r) {
    if (l < r) {
        int m = l + (r - l) / 2;

        mergeSort(arr, l, m);
        mergeSort(arr, m + 1, r);

        merge(arr, l, m, r);
    }
}

int countTriangles(int arr[], int n) {
    // Sorting the input array.
    mergeSort(arr, 0, n - 1);

    int count = 0;

    // Traversing the array.
    for (int i = 0; i < n - 2; ++i) {
        int k = i + 2;

        // Traversing all the elements after i-th index.
        for (int j = i + 1; j < n; ++j) {
            while (k < n && arr[i] + arr[j] > arr[k])
                ++k;

            // Incrementing the count of triangles.
            if (k > j)
                count += k - j - 1;
        }
    }
    return count;
}

class Solution {
  public:
    // Function to count the number of possible triangles.
    int countTriangles(vector<int>& arr) {
        int n = arr.size(); // Get the size of the array

        // Sorting the input array.
        sort(arr.begin(), arr.end());

        int count = 0;

        // Traversing the array.
        for (int i = 0; i < n - 2; ++i) {
            // Storing the third index starting from ith index in k.
            int k = i + 2;

            // Traversing all the elements after ith index.
            for (int j = i + 1; j < n; ++j) {
                // Traversing all the elements after jth index to check if there
                // exists any element which can satisfy the condition of triangle
                // with the elements at ith, jth and kth index.
                while (k < n && arr[i] + arr[j] > arr[k])
                    ++k;

                // Incrementing the count of triangles.
                if (k > j)
                    count += k - j - 1;
            }
        }
        // Returning the count of triangles.
        return count;
    }
};

