    // Function to count the number of possible triangles.
    countTriangles(arr) {
        // Sorting the input array.
        arr.sort((a, b) => a - b);

        let count = 0;
        const n = arr.length; // Get the length of the array

        // Traversing the array.
        for (let i = 0; i < n - 2; ++i) {
            // Storing the third index starting from the ith index in k.
            let k = i + 2;

            // Traversing all the elements after the ith element.
            for (let j = i + 1; j < n; ++j) {
                // Traversing all the elements after the jth index to check if there
                // exists any element that can satisfy the condition of triangle
                // with the elements at ith, jth, and kth index.
                while (k < n && arr[i] + arr[j] > arr[k]) {
                    ++k;
                }

                // Incrementing the count of triangles.
                if (k > j) count += k - j - 1;
            }
        }

        // Returning the count of triangles.
        return count;
    }
}
