/**
 * @param {number} N
 * @returns {number}
 */

class Solution {
    //Function to count number of set bits in the given number N.
    countBits(N)
    {
        let count = 0;
        for(let i = 0; i<31; i++){
            let x = 1 << i; // calculate the ith bit
            let y = Math.floor((N + 1) / (x * 2)) * x; // count the number of set bits up to the ith bit
            let z = (N + 1) % (x * 2) - x; // count the number of set bits in the ith bit
            count += y + Math.max(z, 0); // add the counts to the total
        }
        return count;
    }
}
