class Solution {
  public:
    // Binary search to find the index of the largest element in arr that is <= x
    int binarySearch(int arr[], int l, int h, int x) {
        while (l <= h) {
            int mid = (l + h) / 2;

            // If element at mid is less than or equal to x,
            // move to the right half of the array.
            if (arr[mid] <= x)
                l = mid + 1;
            else
                h = mid - 1;
        }

        // Return the index of the largest element <= x.
        return h;
    }

    // Function to count the number of elements in b that are less than or equal to each
    // element in a
    vector<int> countEleLessThanOrEqual(vector<int>& a, vector<int>& b) {
        // Create a result vector to store the answer
        vector<int> res;

        // Sort array b in ascending order to enable binary search
        sort(b.begin(), b.end());

        // Iterate over each element in a
        for (int i = 0; i < a.size(); i++) {
            // Perform binary search on b to find the count of elements <= a[i]
            int index = binarySearch(b.data(), 0, b.size() - 1, a[i]);

            // Append the count (index + 1) to the result vector
            res.push_back(index + 1);
        }

        // Return the result vector containing counts for each element in a
        return res;
    }
};

class Solution {
    // Function to perform binary search to find the index of the largest element in arr
    // that is <= x
    public static int binarySearch(int arr[], int l, int h, int x) {
        while (l <= h) {
            int mid = (l + h) / 2;

            // If the middle element is less than or equal to x,
            // search in the right half of the array.
            if (arr[mid] <= x)
                l = mid + 1;
            else
                h = mid - 1;
        }

        // Return the index of the largest element less than or equal to x.
        return h;
    }

    // Function to count the number of elements in b that are less than or equal to each
    // element in a
    public static ArrayList<Integer> countEleLessThanOrEqual(int a[], int b[]) {
        // Create an ArrayList to store the result
        ArrayList<Integer> res = new ArrayList<>();

        // Sort array b in ascending order to prepare for binary search
        Arrays.sort(b);

        // Iterate through each element in a
        for (int i = 0; i < a.length; i++) {
            // Perform binary search on b to find the count of elements <= a[i]
            int index = binarySearch(b, 0, b.length - 1, a[i]);

            // Add the result (index + 1) to the ArrayList
            res.add(index + 1);
        }

        // Return the ArrayList containing the counts for each element in a
        return res;
    }
}

