class Solution {
    // Function to count elements in 'a' that are less than or equal to each element in
    // 'b'
    public List<int> countEleLessThanOrEqual(int[] a, int[] b) {
        // Sort the second array 'b'
        Array.Sort(b);

        List<int> res = new List<int>();
        // For each element of the first array 'a', find the count of elements <= a[i]
        // in 'b'
        for (int i = 0; i < a.Length; i++) {
            // Perform binary search to find the index of the last element <= a[i] in
            // 'b'
            int index = BinarySearch(b, a[i]);
            // Add the count (index + 1) to the result list
            res.Add(index + 1);
        }
        return res;
    }

    // Binary search to find the index of the largest element in 'b' that is <= x
    private int BinarySearch(int[] b, int x) {
        int l = 0;
        int h = b.Length - 1;
        while (l <= h) {
            int mid = (l + h) / 2;

            // If b[mid] is less than or equal to x, search the right half
            if (b[mid] <= x) l = mid + 1;
            // Else, search the left half
            else
                h = mid - 1;
        }
        // Return the index of the largest element <= x
        return h;
    }
}

class Solution {
    // Function to perform binary search
    binarySearch(arr, target) {
        let l = 0, r = arr.length - 1;
        while (l <= r) {
            let mid = Math.floor((l + r) / 2);

            // If the middle element is less than or equal to the target,
            // search in the right half of the array.
            if (arr[mid] <= target) {
                l = mid + 1;
            } else {
                r = mid - 1;
            }
        }

        // Return the number of elements less than or equal to the target.
        return r + 1;
    }

    // Function to count the number of elements in b that are less than or equal to each
    // element in a
    countEleLessThanOrEqual(a, b) {
        b.sort((x, y) => x - y); // Sort array b in ascending order

        let result = [];
        // For each element in a, use binary search to find how many elements in b are
        // <= a[i]
        for (let i = 0; i < a.length; i++) {
            result.push(this.binarySearch(b, a[i]));
        }

        return result;
    }
}
