// Back-end complete function Template for Java

class Solution{
    // Function to count the number of tokens in a given pattern and text
    static int getTokens(String pattern, String text){
        int count = 0;
		Pattern tokSplitter = Pattern.compile(pattern);
		Matcher m = tokSplitter.matcher(text);
		while (m.find())
			count++;
		return count;
    }

    // Function to count the number of sentences and words in a given text
    static List<Integer> sentenceWord(String text){
        List<Integer> res = new ArrayList<Integer>();
        // Counting the number of sentences by using the pattern "[^?.!]+"
        int s = getTokens("[^?.!]+", text);
        // Counting the number of words by using the pattern "[a-zA-Z]+"
        int w = getTokens("[a-zA-Z]+", text);
        res.add(s);
        res.add(w);
        return res;
    }
}
