// Function to determine which country is at war
class Solution {

  public:
    string countryAtWar(vector<int>& arr1, vector<int>& arr2) {
        int n = arr1.size();
        long long a_dead = 0; // counter for dead soldiers from country A
        long long b_dead = 0; // counter for dead soldiers from country B

        // Loop through each soldier
        for (long long i = 0; i < n; i++) {
            if (arr1[i] == arr2[i]) // If soldier from A and B are equal in strength
            {
                arr1[i] = arr2[i] = 0; // Both soldiers are dead
                a_dead++;
                b_dead++;
            } else if (arr1[i] > arr2[i]) {
                // If soldier from A is stronger than soldier from B

                arr2[i] = 0;
                // Soldier from B is dead
                b_dead++;
            } else {
                // If soldier from B is stronger than soldier from A

                arr1[i] = 0;
                // Soldier from A is dead
                a_dead++;
            }
        }

        // Determine which country has more dead soldiers
        if (a_dead > b_dead) // Country A has more dead soldiers
        {
            return "B";
        } else if (b_dead > a_dead) // Country B has more dead soldiers
        {
            return "A";
        } else // Both countries have an equal number of dead soldiers
        {
            return "DRAW";
        }
    }
};

