        fastFib(n / 2, ans);
        int a = ans[0]; // F(n)
        int b = ans[1]; // F(n+1)
        int c = 2 * b - a;
        c = a * c;             // F(2n)
        int d = a * a + b * b; // F(2n + 1)

        if (n % 2 == 0) {
            ans[0] = c;
            ans[1] = d;
        } else {
            ans[0] = d;
            ans[1] = c + d;
        }
    }

    public int totalAnimal(int n) {
        int[] ans = new int[2];
        fastFib(n + 1, ans);
        return ans[0];
    }
}
