//Back-end complete function Template for C++
class Solution {
  public:
//Function to calculate the Nth term in the series.
long double NthTerm(int N) 
{
    long double res;
    //Checking if N is even or odd.
    //If even, calculate the term using the formula pow(2,pow(3,N/2-1)).
    //If odd, calculate the term using the formula pow(2,pow(2,N/2)).
    if(!(N%2))
        res=(long double)pow(2,(long long int)pow(3,N/2-1));
    else
        res=(long double)pow(2,(long long int)pow(2,N/2));
    //Returning the Nth term.
    return res;
}
};
