//Back-end complete function Template for C++
class Solution {
  public:
    //Function to count the number of distinct values.
    int distinctValues(int n, int a, int b, int c, int d){
        int cnt=0;
        //iterating over all possible values of i.
        for(int i=1;i<=n;i++){
            //calculating the values of y, w, and z based on given equations.
            int y=c-b+i;
            int w=d-a+i;
            int z=y+d-a;

            //checking if the calculated values belong to the valid range.
            //if yes, increment the count.
            if (y>=1 and y<=n and z>=1 and z<=n and w>=1 and w<=n)cnt++;
        }
        //returning the count of distinct values.
        return cnt;
    }
};

