class Solution
{
    //Complete this function
    //Function for finding the distinct values in an array
    public int distinctValues(int n, int a, int b, int c, int d)
    {
        int cnt=0;
        //iterating over all possible values of i.
        for(int i=1;i<=n;i++){
            //calculating the values of y, w, and z based on given equations.
            int y=c-b+i;
            int w=d-a+i;
            int z=y+d-a;

            //checking if the calculated values belong to the valid range.
            //if yes, increment the count.
            if (y>=1 && y<=n && z>=1 && z<=n && w>=1 && w<=n)
                cnt++;
        }
        //returning the count of distinct values.
        return cnt;
    }
}
