class Solution{
	public:
	//Function to calculate the average runs based on given runs and status.
	int Average(vector<int>run, vector<string>status){
		int cnt = 0; // variable to count the number of wickets
		int tot = 0; // variable to store the total runs

		//iterating through each run and status.
		for(int i = 0; i < run.size(); i++){
			if(status[i] == "out")
				cnt++; //incrementing the wicket count if status is "out"
			tot += run[i]; //adding the current run to the total sum
		}
		if(!cnt)
			return -1; //returning -1 if no wickets are taken
		return ceil((tot*1.0)/cnt); //returning the average by dividing total runs by number of wickets
	}
};
