  bool isprime(int n){
    for(int i = 2; i*i <= n ;i++){ 
            if(n%i == 0){ 
                return false; 
            } 
        }
    return true; 
  }

  // Function to check if a number is Damon Prime or not
  string damonPrime(int n){
    if(n<=2) return "No"; // Damon Prime numbers cannot be less than or equal to 2
    else{
        if(isprime(n-1) and isprime(n+1)) return "Yes"; // If n-1 and n+1 are both prime, then n is Damon Prime
