//Back-end complete function Template for C++

class Solution {
  public:
    // Function to determine the size of different data types.
    int dataTypeSize(string str) {
        if (str == "Character")
            return sizeof(char);
        else if (str == "Integer")
            return sizeof(int);
        else if (str == "Float")
            return sizeof(float);
        else if (str == "Long")
            return sizeof(long);
        else if (str == "Double")
            return sizeof(double);
        else
            return -1; // Return -1 if the input data type is not valid.
    }
};

//Back-end complete function Template for Java

class Solution {
    // Function to calculate the size of a given data type.
    static int dataTypeSize(String str) {
        // Check if the given data type is Character.
        if (str.equals("Character")) return 1;
        // Check if the given data type is Integer.
        else if (str.equals("Integer"))
            return 4;
        // Check if the given data type is Float.
        else if (str.equals("Float"))
            return 4;
        // Check if the given data type is Long.
        else if (str.equals("Long"))
            return 8;
        // Check if the given data type is Double.
        else if (str.equals("Double"))
            return 8;
        // Return -1 if the data type is not recognized.
        else
            return -1;
    }
}

