class Solution {
    // Function to find the size of different data types.
    dataTypeSize(str) {
        // if the data type is Integer, return size 4.
        if (str === 'Integer') {
            return 4
        }
        // if the data type is Character, return size 1.
        else if (str === 'Character') {
            return 1;
        }
        // if the data type is Float, return size 4.
        else if (str === 'Float') {
            return 4;
        }
        // if the data type is Long or Double, return size 8.
