#include <iostream>
using namespace std;

class Solution {
  public:

    // Helper function to calculate the day of the week for a given date.
    // Returns an integer from 0 to 6, where 0 denotes Sunday, 1 denotes Monday and so on.
    int date(int d, int m, int y) {
        static int t[] = {0, 3, 2, 5, 0, 3, 5, 1, 4, 6, 2, 4};
        y -= m < 3;
        return (y + y/4 - y/100 + y/400 + t[m-1] + d) % 7;
    }

    // Function to get the day of the week for a given date.
    string getDayOfWeek(int d, int m, int y) {
        int day = date(d, m, y);

        // Checking the day of the week and returning the corresponding string.
        if(day == 0)
            return("Sunday");
        if(day == 1)
            return("Monday");
        if(day == 2)
            return("Tuesday");
        if(day == 3)
            return("Wednesday");
        if(day == 4)
            return("Thursday");
        if(day == 5)
            return("Friday");
        if(day == 6)
            return("Saturday");
    }
};

