//Back-end complete function Template for Java
import java.util.Scanner;

class Main {
    public static void main(String[] args) {
        Scanner scn = new Scanner(System.in);
        int d = scn.nextInt();
        int n = scn.nextInt();
        int x = n % 7;
        int ans = d - x;
        if (ans < 0) {
            ans += 7;
        }
        System.out.println(ans);
    }
}
