#include <iostream>
using namespace std;

int main() {
    int N;
    cin >> N;
    int binaryNum[1000];
    int B = N;

    int i = 0;

    if (B == 0) {
        cout << ("0");
    }

    while (B > 0) {

        // storing remainder in binary array
        binaryNum[i] = B % 2;
        B = B / 2;
        i++;
    }

    for (int j = i - 1; j >= 0; j--)
        cout << (binaryNum[j]);

    return 0;
}
