//Back-end complete function Template for C++

list<int> l;

void insert(int x) {
    l.push_back(x);
}

void print() {
    for (int x : l) {
        cout << x << " ";
    }
    cout << endl;
}

void replace(int x, vector<int> sequence) {

    auto it = find(l.begin(), l.end(), x);

    if (it == l.end())
        return;

    it = l.erase(it);

    l.insert(it, sequence.begin(), sequence.end());
}
